def get_keyword(ciphertext, key_len):
    letters_by_frequency = 'EARIOTNSLCUDPMHGBFYWKVXZJQ'
    keyword = ''
    for i in range(key_len):
        text = ciphertext[i::key_len]
        sorted_letters = sorted(letters_by_frequency, key=text.count, reverse=True)
        print(sorted_letters)
        shifts = [ord(k) - ord(letters_by_frequency[j]) for j, k in enumerate(sorted_letters)]
        print(sorted(shifts))
        shift = max(shifts, key=shifts.count)
        print(shift)
        keyword += chr(shift % 26 + 65)

    return keyword


